function main({ data1 }) {
  // 创建一个空对象来存储结果
  const result = {};
  const data = JSON.parse(data1);

  // 遍历数组中的每个故事对象
  data.forEach(story => {
    // 从故事对象中提取ID和对应的描述
    const id = Object.keys(story)[0];
    const description = story[id];

    // 将ID和描述作为键值对添加到结果对象中
    result[id] = description;
  });

  // 返回包含所有键值对的对象
  return {
    ID1: result.ID1,
    ID2: result.ID2,
    ID3: result.ID3,
    ID4: result.ID4,
    ID5: result.ID5,
    ID6: result.ID6,
    ID7: result.ID7,
    ID8: result.ID8,
    ID9: result.ID9,
    ID10: result.ID10
  };
}

