function main({ data1 }) {
  // 创建一个空对象来存储结果
  const result = {};
  const data = JSON.parse(data1);

  // 遍历数组中的每个故事对象
  data.forEach(story => {
    // 从故事对象中提取ID和对应的描述
    const id = Object.keys(story)[0];
    const description = story[id];

    // 将ID和描述作为键值对添加到结果对象中
    result[id] = description;
  });

  // 返回包含所有键值对的对象
  return {
    URL: result.ID1,
    TITLE: result.ID2,
    AUTHOR: result.ID3,
    COPYWRITER: result.ID4,

  };
}

