function main({data1}) {
  // 将字符串分割成数组
  var dataArray = data1.split(', ');

  // 获取数组的长度
  var arrayLength = dataArray.length;

  // 生成一个随机索引
  var randomIndex = Math.floor(Math.random() * arrayLength);

  // 返回随机索引对应的数组元素
  return { result: dataArray[randomIndex] };
}

