inputs.value:
function main({data1}) {
  // 正则表达式匹配JSON对象中的q属性值
  const jsonPartRegex = /"q":"([^"]*)"/;
  const match = data1.match(jsonPartRegex);

  if (match && match[1]) {
    // 提取q属性的值，即JSON字符串部分
    const jsonString = match[1];

    // 使用正则表达式匹配中文字符
    const chineseText = /[\u4e00-\u9fa5]+/g.test(jsonString) ? jsonString.match(/[\u4e00-\u9fa5]+/g).join('') : '';

    // 返回包含中文文本的对象
    return { result: chineseText };
  } else {
    // 如果没有找到匹配的JSON字符串部分，返回空结果
    return { result: '' };
  }
}

