function main({data1}) {
  // 将字符串分割成数组
  var dataArray = data1.split(',');

  function getRandomElements(arr, num) {
    let result = [];
    let arrayCopy = [...arr];  // 复制原始数组以避免修改

    for (let i = 0; i < num; i++) {
        const randomIndex = Math.floor(Math.random() * arrayCopy.length);
        result.push(arrayCopy[randomIndex]);
        arrayCopy.splice(randomIndex, 1);  // 移除已经选中的元素
    }

    return result;
  }
  const randomElements = getRandomElements(dataArray, 7);

  // 返回随机索引对应的数组元素
  return { result: randomElements.join(',') };
}

