	// 使用 map 提取 Data 列中的数据
	const dataValues = jsonArray.map(item => {
	  const match = item.q.match(dataRegex); // 使用正则表达式匹配第三列 Data
	  return match ? match[1].trim() : ''; // 如果匹配成功，返回 Data 列中的值
	});

	// 将 Data 列的数据拼接为一个字符串，使用 \n 进行分割
	const resulttext = dataValues.join('\n');

	// 打印结果
	return { result: resulttext }
}

