import json

def chat_request(info:dict, question):

    client = OpenAI(
    # 若没有配置环境变量，请用百炼API Key将下行替换为：api_key="sk-xxx",
    api_key=info['api_key'], 
    base_url=info["base_url"],
    )

    completion = client.chat.completions.create(

        model=info['model'], # 模型列表：https://help.aliyun.com/zh/model-studio/getting-started/models
        messages=[
            {'role': 'system', 'content': 'You are a helpful assistant.'},
            {'role': 'user', 'content': f'{question}'}],
        )
    
    data_str = completion.model_dump_json()
    data_json = json.loads(data_str)
    result = data_json["choices"][0]['message']['content']
    output = {"提示词":"".join(question)}
    match info['ISP']:
        case "Aliyun":
            output[f"阿里云_{info['model']}"] = result
        case "Zhipu":
            output[f"智谱_{info['model']}"]= result
        case "Huoshan":
            output[f"扣子_{info['model']}"]=result
    return output
    
