function main({ product_info, userInput }) {
  // 解析 JSON 对象，假设已经将 product_info 传递为对象参数
  const dataObject = JSON.parse(product_info);

  // 从 dataObject 获取 resultList 数组
  const resultList = dataObject.resultList || [];

  // 初始化返回对象
  const result = { findData: null, unfind: "数据输入错误", userOption: null };

  // 遍历 resultList 寻找匹配项
  for (const product of resultList) {
    // 遍历 product 对象中的所有键值对
    for (const key in product) {
      // 将 product[key] 转换为字符串再进行比较
      if (String(product[key]) === userInput) {
        // 如果找到匹配项，设置 findData 并清除 unfind 消息
        result.findData = product.Product_ID;
        result.userOption = product.Product_Name;
        result.unfind =null;
        result['productNumber'] = product.Product_Number
        return result;  // 立即返回结果
      }
    }
  }

  // 如果没有匹配项，返回默认的错误消息
  return result;
}

