  let parsedData = JSON.parse(data1);

  // 遍历 parsedData.result
  for (let i = 0; i < parsedData.result.length; i++) {
    let value = parsedData.result[i];
    if (String(i + 1) === userInput || value.Product_Color === userInput) {
      // 如果找到匹配项，直接返回结果
      return { findData: value.Product_Color };
